import { Component, EventEmitter, Output } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Album } from '../album';
import { AlbumService } from '../album.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  word: string = '';

  @Output() searchAlbums: EventEmitter<Album[]> = new EventEmitter(); // émetteur d'évenement
  constructor(
    private albumService: AlbumService
  ) {}

  onSubmit(form: NgForm) {
    const results: Album[] = this.albumService.search(form.value.word);
    this.searchAlbums.emit(results);
  }

  onChangeEmit($event: string) {
    const results: Album[] = this.albumService.search($event);
    this.searchAlbums.emit(results);
  }
}
<form class="form-inline my-2 my-lg-0 search" (ngSubmit)="onSubmit(formSearch)" #formSearch="ngForm">
  <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" name="word" [(ngModel)]="word" (ngModelChange)="onChangeEmit($event)" required>
  <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
</form>
